-- ----------------------------
-- Table structure for book_name
-- ----------------------------
DROP TABLE IF EXISTS `book_name`;
CREATE TABLE `book_name` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '小说ID',
  `name` varchar(50) NOT NULL COMMENT '小说名字',
  `introduction` text NOT NULL COMMENT '小说简介',
  `author` varchar(20) NOT NULL COMMENT '小说作者',
  `cover` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of book_name
-- ----------------------------
INSERT INTO `book_name` VALUES ('1', '贼警', '面对团伙化，境外化，高智商化犯罪袭击和报复，重案七组几近解散。\r\n左罗临危受命成为七组组长，以水逆计划招募苏诚，精诚合作，取长补短，重建七组辉煌。\r\n但作为不法之徒的苏诚会真心帮助左罗吗？\r\n全面布局，奇思妙想，奇案怪案，科技犯罪。螳螂捕蝉，黄雀在后，赢输只在一念间。展现游走在生死边缘线的男人友情，一切尽在本书中。\r\n贼警一词出自水浒等书，是贼人来袭的警报，非贼和警察，也不是很贼的警', '山水浮屠', './assets/book/1.jpg');
INSERT INTO `book_name` VALUES ('2', '我要做皇帝', '受命于', '唐家三少', './assets/book/2.jpg');
INSERT INTO `book_name` VALUES ('3', '神话版三国', '陈曦看着将一块数百斤巨石撇出去的士卒，无语望苍天，这真的是东汉末年？\r\n　　吕布单枪匹马凿穿万人部队，这怎么看都不科学。\r\n　　赵子龙真心龙魂附体了，一剑断山，这真的是人？\r\n　　典韦单人护着曹操杀出敌营，顺手宰了对面数千步骑，这战斗力爆这是不是哪里有些不对啊，陈曦顺手摸了一把鹅毛扇挥了一下，狂风大作，叹了一口气，“这是神话吧，我自己都不正常了。”\r\n　　ps：其实这是一篇正经的种田文……', '我吃西红柿', './assets/book/3.jpg');
INSERT INTO `book_name` VALUES ('4', '三国之超级召唤系统', '现代历史系大学生穿越到汉末，成为刘辩。刘边表示压力山大，此时董魔王已经入洛阳，皇位即将被废，要不了多久就要被一杯毒酒鸩杀。不怕，我有超级召唤系统。第一任务，除董卓，保住皇位。你有天下第一的吕布？我有白衣神将薛仁贵，天纵神威杨再兴。不行，让我想想先召唤哪个！', '老王', './assets/book/4.jpg');
INSERT INTO `book_name` VALUES ('5', '兵甲三国', '给我一个姑娘，我创造不了一个民族；给我一块木头，我能瞬间制造出一把连弩。\r\n　　兵甲系统在手，三国天下我有！\r\n　　并州狼骑又如何？且看我三千铝盔铝甲的白马义从如何马踏天下！\r\n　　河北先登又如何？且看我怒锋营手执钢制弹簧连弩如何荡净九州！\r\n　　江东水军又如何？且看我装备连发火炮的幽燕车船如何纵横四海！\r\n　　……\r\n　　醉卧美人膝，醒掌天下权，穿越成公孙瓒庶子公孙白，起于幽州，席卷天下。', '汉风熊烈', './assets/book/5.jpg');
INSERT INTO `book_name` VALUES ('6', '大魏能臣', '萧逸，字无愁，命犯贪狼，犹如一颗美丽的流星般划过东汉末年历史的天空，搅动漫天风云……\r\n　　曹操曰：“吾得无愁，天下无愁！”刘备曰：“有萧逸在，中原无我立锥之地！孙权曰：“萧逸不死，孤心不安！司马懿曰：“吾若不取，则天下归萧逸矣！”有诗为证：\r\n　　卧龙凤雏隐荆襄，\r\n　　俊美幼麟是周郎，\r\n　　萧墙之内伏冢虎；\r\n　　天意茫茫属萧郎！', '小半身', './assets/book/6.jpg');
INSERT INTO `book_name` VALUES ('7', '天道图书馆', '张悬穿越异界，成了一名光荣的教师，脑海中多出了一个神秘的图书馆。\r\n　　只要他看过的东西，无论人还是物，都能自动形成书籍，记录下对方各种各样的缺点，于是，他牛大了！\r\n　　教学生、收徒弟，开堂授课，调教最强者，传授天下。\r\n　　“灼阳大帝，你怎么不喜欢穿内裤啊？堂堂大帝，能不能注意点形象？”\r\n　　“玲珑仙子，你如果晚上再失眠，可以找我嘛，我这个人唱安眠曲很有一套的！”\r\n　　“还有你，乾坤魔君，能不能少吃点大葱，想把老子熏死吗？”\r\n　　这是一个师道传承，培养、指点世界最强者的牛逼拉风故事。\r\n', '我吃红罗布', './assets/book/7.jpg');
INSERT INTO `book_name` VALUES ('8', '龙王传说', '伴随着魂导科技的进步，斗罗大陆上的人类征服了海洋，又发现了两片大陆。魂兽也随着人类魂师的猎杀无度走向灭亡，沉睡无数年的魂兽之王在星斗大森林最后的净土苏醒，它要带领仅存的族人，向人类复仇！\r\n　　唐舞麟立志要成为一名强大的魂师，可当武魂觉醒时，苏醒的，却是……', '梦入神机', './assets/book/8.jpg');
INSERT INTO `book_name` VALUES ('9', '美食供应商', '“在遥远的东方，存在着一个数次拒绝了米其林三星评价的奇怪小店。 \r\n　　那里价格昂贵，一碗配汤蛋炒饭288RMB，哦忘了还有一碟泡菜，但就算是这样也有很多人排队等候。\r\n　　那里不接受预定，只接受本人现场排队，无数人开着专机专程排队，当然并不提供停机位或停车位。 \r\n　　那里服务恶劣，竟然让顾客自己端菜收拾碗筷，对了还要擦桌子，哦！上帝这老板简直疯了。” \r\n　　——《米其林美食杂志》\r\n', '江南烟雨', './assets/book/9.jpg');
INSERT INTO `book_name` VALUES ('10', '银狐', '人的第一要求就是活着，第二要求还是活着，第三要求依旧是活着……在某些特定的环境下，活着就成了一种奢望。\r\n　　在地狱中我们仰望天堂，把手伸出去，虽然不能触碰到天堂，却能让我们距离天堂更近一些。\r\n　　在地狱里歌唱，在地狱里感恩，在地狱里相爱，在地狱里相杀，我们流着眼泪相互簇拥而后将匕首刺进对方的胸腹，不为别的，只是为了哪一丝丝若有若无的感恩。\r\n　　大宋的世界是我的羁绊，也是我的天空，只有振翅飞翔的鸟儿才晓得天空的含义。\r\n　　皇帝说——借我皇家广厦一角，与你母子安身。\r\n　　铁心源说——滴水之恩我当涌泉相报，不过你不能要求，我给你什么样的报答，你接受就是了。\r\n　　我可能比你们所有人都聪明，从某种意义上来说，我就是先知，我就是哲人，我就是神！', '我爱吃土豆', './assets/book/10.jpg');

